languages = ["python","swift","C++"]
# ACCESSING ITEMS AT INDEX 0
print(languages[-1]) #C++
# LIST SLICING IN PYTHON
my_list = ['p','r','o', 'g','r','a','m','m','i','n','g']
# items from index 2 to index 7
print(my_list[2:6]) # output:['o','g','r','a']
#items from index 6 to end
print(my_list[6:]) #output: ['m','m','i','n','g']
#items beginning to the end
print(my_list[:]) #output: ['p','r','o','g','r','a','m','m','i','n','g']
#access items at index 2
print(languages[-3]) #output:python